import './ListaOpcionesEjemplo.css'
function ListaOpcionesEjemplo() {
    const equipos=["Programacion","Front End","Data Science","DevOps","UX y Diseño","Móvil","Innovación y Gestión"];
    return (
        <div className="lista-opciones">
            <label htmlFor="equipo">Equipo</label>
            <input id="equipo" list="lista__equipo" placeholder="Lista Equipo"/>
            <datalist id="lista__equipo">
                {equipos.map((equipo,index)=>{
                    return <option value={equipo} key={index}></option>
                })}
            </datalist>
        </div>
    )
}
export default ListaOpcionesEjemplo
