import './CardEquipoEjemplo.css'
import { AiOutlineHeart, AiFillHeart } from 'react-icon/ai'
function CardEquipoEjemplo(props) {
    const {id,nombre,puesto,foto,equipo}=props.registro;
    const {colorHeader,eliminarColaborador}=props;
    const colorFondo={backgroundColor:colorHeader};
    const eliminar=()=>{
        eliminarColaborador(id);
    }
    return (
        <article className="card">
            <div className="card__header" style={colorFondo}>
                <span onClick={eliminar}>x</span>
                <img src={foto} alt=""/>
            </div>
            <div className="card__body">
                <h4>{nombre}</h4>
                <p>{puesto} y equipo {equipo}</p>
            </div>
        </article>
    )
}
export default CardEquipoEjemplo
