import { useState } from 'react'
import './FormularioEjemplo.css'
import CampoTextoEjemplo from '../CampoTextoEjemplo/CampoTextoEjemplo'
import ListaOpcionesEjemplo from '../ListaOpcionesEjemplo/ListaOpcionesEjemplo'
import Boton from '../Boton/Boton'
function FormularioEjemplo() {
    const manejarEnvio=(evento)=>{
        evento.preventDefault();
    }
    const [getNombre,setNombre]= useState("");
    const [getPuesto,setPuesto]= useState("");
    const [getFoto,setFoto]= useState("");
    const actualizarInputNombre= (eventoInput)=>{
        setNombre(eventoInput.target.value);
    }
    const actualizarInputPuesto= (eventoInput)=>{
        setPuesto(eventoInput.target.value);
    }
    const actualizarInputFoto= (eventoInput)=>{
        setFoto(eventoInput.target.value);
    }
    return (
        <section className="formulario">
            <form onSubmit={manejarEnvio}>
                <h2>Rellena el formulario para crear el colaborador.</h2>
                <CampoTextoEjemplo titulo="nombre" placeholder="Digite su nombre" valorInput={actualizarInputNombre}/>
                <CampoTextoEjemplo titulo="Puesto" placeholder="Digite su puesto" valorInput={actualizarInputPuesto}/>
                <CampoTextoEjemplo titulo="Foto" placeholder="Inserte el enlace de su foto" valorInput={actualizarInputFoto}/>
                <ListaOpcionesEjemplo />
                <Boton texto="crear"/>
            </form>
        </section>
    )
}
export default FormularioEjemplo
